import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from '../../contexts/AuthContext';
import axios from 'axios';
import "./CreateMessenger.css";

const Create = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [isPending, setIsPending] = useState(false);
    const [error, setError] = useState(null);

    const navigate = useNavigate();
    const { user } = useContext(AuthContext);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!user) {
            setError("You must be logged in to create a messenger");
            return;
        }

        const messenger = {
            title,
            description,
            creator: user.username
        }

        setIsPending(true);

        try {
            await axios.post('http://localhost:5000/messengers', messenger);
            console.log('New messenger added');
            setIsPending(false);
            navigate('/');
        } catch (err) {
            setError(err.message);
            setIsPending(false);
        }
    }

    const handleClose = () => {
        navigate('/');
    }

    return (
        <div className="create-messenger window">
            <div className="title-bar">
                <span className="title">Add a new messenger</span>
                <button className="close-button" onClick={handleClose}>âœ•</button>
            </div>
            <div className="window-body">
                {error && <div className="error-box">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <label htmlFor="title">Messenger Title:</label>
                    <input
                        type="text"
                        id="title"
                        required
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                    <label htmlFor="description">Messenger Description:</label>
                    <textarea
                        id="description"
                        required
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    ></textarea>
                    <div className="field">
                        <span>Creator: {user ? user.username : 'Not logged in'}</span>
                    </div>
                    <button type="submit" className="retro-button" disabled={isPending}>
                        {!isPending ? 'Add Messenger' : 'Loading...'}
                    </button>
                </form>
            </div>
        </div>
    );
}
 
export default Create;
